mapper.select:
SELECT ID, NAME, LOCATION, CONTENT, PERSON, FOLDER, ISPRIVATE FROM ARTICLE WHERE ISPRIVATE = #{isPrivate, jdbcType=BIT} AND USERID = #{userId, jdbcType=SMALLINT}
AND NAME LIKE '%${q, jdbcType=VARCHAR}%' OR LOCATION LIKE '%${q, jdbcType=VARCHAR}%' OR PERSON LIKE '%${q, jdbcType=VARCHAR}%' OR CONTENT LIKE '%${q, jdbcType=VARCHAR}%' OR FOLDER LIKE '%${q, jdbcType=VARCHAR}%'
ORDER BY MODIFY DESC

SELECT ID, NAME, LOCATION, PERSON, FOLDER, DESCRIPTION, TITLE, CREATED, MODIFY FROM ARTICLE WHERE ISPRIVATE = #{isPrivate, jdbcType=BIT} AND USERID = #{userId, jdbcType=SMALLINT}
AND FOLDER = #{folder, jdbcType=VARCHAR}
AND LOCATION = #{location, jdbcType=VARCHAR}
AND PERSON = #{person, jdbcType=VARCHAR}
ORDER BY MODIFY DESC

mapper.update:
UPDATE ARTICLE
NAME=#{name}, LOCATION=#{location}, CONTENT=#{content}, PERSON=#{person}, FOLDER=#{folder}, ISPRIVATE=#{isPrivate}, USERID=#{userId}, DESCRIPTION=#{description}, TITLE=#{title}, MODIFY = now()
WHERE ID = #{id}

mapper.select:
SELECT ONE.*,c.comments FROM (SELECT a.id AS id, a.name AS name, a.description AS description, a.userId AS userId, a.modify AS modify, a.title as title, u.nick_Name AS nickName, info.photoId AS photoId FROM article a, t_user u, u_info info WHERE a.userId = u.id AND u.id = info.userId ) ONE LEFT JOIN (SELECT articleId, COUNT(*) comments FROM a_comments GROUP BY articleId) c ON ONE.id = c.articleId where 1=1
AND ONE.userId = #{userId}
AND ONE.id = #{id}
order by ONE.modify DESC
